package com.flowcontrols;

public class PatternEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 5; i++)// rows
		{
			for (int j = 0; j < 5; j++) // columns
			{
				System.out.print("*" + " "); // * * * * *
			}
			System.out.println();
		}
	}

}
